import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
const App = () => {
  const [file, setFile] = useState(null);
  const [numThreads, setNumThreads] = useState(4);
  const [results, setResults] = useState([]);
  const [res, setRes] = useState(false);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleNumThreadsChange = (event) => {
    setNumThreads(parseInt(event.target.value));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`http://localhost:3001/process?threads=${numThreads}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setResults(response.data.results);
      console.log(response.data.results); // Check the structure of results in the console
      if (results != null) {
        setRes(true);
        console.log("hi");
      }
    } catch (error) {
      console.error('Error:', error);
      // Handle the error here
    }
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit} className="form">
        <input type="file" onChange={handleFileChange} className="form-control" />
        <input type="number" value={numThreads} onChange={handleNumThreadsChange} className="form-control" />
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>

      {res && (
        <div className="results">
          <div>
            <h2>Results:</h2>
            <ul className="list-group">
              {results.results.map((threadResult, index) => (
                <li key={index} className="list-group-item">
                  <strong>{Object.keys(threadResult)[0]}</strong>
                  <ul className="list-group">
                    {Object.values(threadResult)[0].map((wordObj, idx) => (
                      <li key={idx} className="list-group-item">
                        {`${Object.keys(wordObj)[0]}: ${Object.values(wordObj)[0]}`}
                      </li>
                    ))}
                  </ul>
                </li>
              ))}
            </ul>
          </div>
          {res && (
            <div>
              <h2>Final Result:</h2>
              <ul className="list-group">
                {results['Final Result'].map((wordObj, idx) => (
                  <li key={idx} className="list-group-item">
                    {`${Object.keys(wordObj)[0]}: ${Object.values(wordObj)[0]}`}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
