import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [file, setFile] = useState(null);
  const [numThreads, setNumThreads] = useState(4);
  const [results, setResults] = useState([]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleNumThreadsChange = (event) => {
    setNumThreads(parseInt(event.target.value));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`http://localhost:3001/process?threads=${numThreads}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setResults(response.data.results);
      console.log()
    } catch (error) {
      console.error('Erreur lors de la requête:', error);
      // Gérer l'erreur ici
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <input type="number" value={numThreads} onChange={handleNumThreadsChange} />
        <button type="submit">Envoyer</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Résultats</th>
          </tr>
        </thead>
        <tbody>
          {results.map((threadResult, index) => (
            <tr key={index}>
              <td>{`Thread ${index}`}</td>
              <td>
                <ul>
                  {threadResult[`Thread ${index}`].map((wordObject, idx) => (
                    <li key={idx}>{`${Object.keys(wordObject)[0]}: ${Object.values(wordObject)[0]}`}</li>
                  ))}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default App;
