import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [file, setFile] = useState(null);
  const [numThreads, setNumThreads] = useState(4);
  const [results, setResults] = useState([]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleNumThreadsChange = (event) => {
    setNumThreads(parseInt(event.target.value));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`http://localhost:3001/process?threads=${numThreads}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setResults(response.data.results);
    } catch (error) {
      console.error('Erreur lors de la requête:', error);
      // Gérer l'erreur ici
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <input type="number" value={numThreads} onChange={handleNumThreadsChange} />
        <button type="submit">Envoyer</button>
      </form>

      <div>
        {results.map((threadResult, index) => (
          <div key={index}>
            <h3>{`Thread ${index}`}</h3>
            <ul>
              {threadResult.map((result, idx) => (
                <li key={idx}>
                  {Object.keys(result).map((key) => (
                    <div key={key}>
                      {`"${key}": ${result[key]}`}
                    </div>
                  ))}
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
