import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [file, setFile] = useState(null);
  const [numThreads, setNumThreads] = useState(4);
  const [results, setResults] = useState([null]);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleNumThreadsChange = (event) => {
    setNumThreads(parseInt(event.target.value));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post(`http://localhost:3001/process?threads=${numThreads}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setResults(response.data.results);
      console.log(response.data.results); // Check the structure of results in the console
    } catch (error) {
      console.error('Error:', error);
      // Handle the error here
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} />
        <input type="number" value={numThreads} onChange={handleNumThreadsChange} />
        <button type="submit">Submit</button>
      </form>

      {results && (
      <div>
      <h2>Results:</h2>
    <ul>

      {results.results.map((threadResult, index) => (
        <li key={index}>
          <strong>{Object.keys(threadResult)[0]}</strong>
          <ul>
            {Object.values(threadResult)[0].map((wordObj, idx) => (
              <li key={idx}>
                {`${Object.keys(wordObj)[0]}: ${Object.values(wordObj)[0]}`}
              </li>
            ))}
          </ul>
        </li>
      ))}
    </ul>

    {results['Final Result'] && (
      <div>
    <h2>Final Result:</h2>
    <ul>
      {results['Final Result'].map((wordObj, idx) => (
        <li key={idx}>
          {`${Object.keys(wordObj)[0]}: ${Object.values(wordObj)[0]}`}
        </li>
      ))}
    </ul>
    </div>
        )}  </div>
        )}
      </div>
  );
};

export default App;
